import React, { createContext, useContext, useReducer } from 'react';
import "../Styles/Store.scss";
import AddOnButton from "./AddOnButton";
import RobotCounterContext from "../Contexts/robotCounterContext.jsx"


const initialState = 0;

const reducer = (state, action) => {
    switch (action.type) {
      case 'WATER_INCREMENT':
        return {
          ...state,
          [action.name]: state[action.name] + 1,
        };
      case 'VEGETATION_INCREMENT':
        return {
          ...state,
          [action.name]: state[action.name] + 20,
        };
      default:
        return state;
    }
  };  


const RobotCountProvider = ({ children }) => {
  const [robotCounter,setRobotCounter] = useReducer(reducer, initialState);
  return (
          <RobotCounterContext.Provider value ={[robotCounter,setRobotCounter]}>
            <RobotGenerator />
                {children}
            </RobotCounterContext.Provider>

  );
};



  const Store = () => {
    const [count, dispatch] = useReducer(reducer, initialState);
    return (
      <div>
        {count}
        <button onClick={() => dispatch('WATER_INCREMENT')}><h2>WATER</h2></button>
        <button onClick={() => dispatch('VEGETATION_INCREMENT')}><h2>VEGETATION</h2></button>

      </div>
    );
  };


const useValue = () => useReducer(reducer, initialState);

const AddonContext = createContext(null);

const useGlobalState = () => {
  const value = useContext(AddonContext);
  if (value === null) throw new Error('Please buy an add-on in the Store');
  return value;
};

const GlobalStateProvider = ({ children }) => (
  <Context.Provider value={useValue()}>{children}</Context.Provider>
);

const Counter = ({ name }) => {
    const [state, dispatch] = useGlobalState();
    return (
      <div>
        {state[name]}
        <button onClick={() => dispatch({ type: 'WATER_INCREMENT', name })}>+1</button>
        <button onClick={() => dispatch({ type: 'VEGETATION_INCREMENT', name })}>-1</button>
      </div>
    );
  };
