import skyscrapers from "../resources/housing.png";
import dino from "../resources/dinosaur-button";
import windmill from "../resources/windmill_faster.gif";

const addonObjects = {

    water: {
       amount: 0,
       rps:1,
       cost:10,
       imgSrc: [cloud, water]
    },
    veg: {
       amount:0,
       rps:4,
       cost:500,
       imgSrc: [forest]
     },
    house: {
      amount:0,
      rps:8,
      cost: 1000,
      imgSrc: [tippi, skyscrapers ]
    },
    dinos: {
      amount: 0,
